INSERT INTO `dou_page` (`id`, `unique_id`, `parent_id`, `page_name`, `content`, `keywords`, `description`) VALUES
(1, 'about', 0, '公司简介', '<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\n	耐耐云商科技有限公司成立于2014年12月。\n</p>\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\n	公司与国内外众多知名生产资料企业建立了良好、稳固的合作伙伴关系。公司逐步形成强大的人才、技术和品牌优势， 并拥有一支朝气蓬勃，技术全面的团队，为适应公司快速发展和迈向国际化的需要，集团总部于2015年5月15日正式迁址上海市浦东新区唐镇电子商务创新港，现在北京、河北唐山、山西阳泉设立三大运营中心，并形成了保险、金融、市场、资源、技术五大支撑点。\n</p>\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\n	“聚合民族工业精髓，创建全球工业市场化服务平台”是公司的经营宗旨，在该宗旨的指导下，打造国内首家生产资料综合包含工业相关资料、制品及设备的电子交易系统、仓储物流服务系统以及互联网增值服务系统等。\n</p>\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\n	电子交易系统以竞拍、竞买、现价、邀约等新颖的交易方式呈献给各界同仁，共同分享世界财富。同时平台为客户提供 免费的技术支持、行业资讯、企业管理咨询等增值服务，持续向社会展示工业企业的新产品、新技术、新项目、新文化、新行情，促成企业资源共享，实现企业价值最大化。\n</p>\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\n	公司在竞争中求发展，在挑战中谋机遇，我公司会为您提供最优质的产品、最先进的技术和最完善的售后服务。愿与您携手并进、共创辉煌，实现中国工业企业的振兴！\n</p>', '公司简介', '公司简介'),
(7, 'product', 0, '产品中心', '', '', ''),
(8, 'service', 0, '服务中心', '<h5 style="font-size:14px;color:#666666;text-indent:2em;font-family:Simsun;">\r\n	免费服务\r\n</h5>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	系列产品随机包含一年的免费服务，服务内容包括一年的系统软件升级服务、一年的硬件保修服务和一年的技术支持服务，服务起止时间从购买合同签订时间起到第365天。\r\n</p>\r\n<h5 style="font-size:14px;color:#666666;text-indent:2em;font-family:Simsun;">\r\n	技术支持服务\r\n</h5>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	对于所有服务期内的，所有的客户服务请求都会进入耐耐云商售后服务监控系统。作为故障排除流程，所有的请求和针对问题所发生的操作都会记录在服务监控系统中。售后服务监控系 统会不间断的更新以反映问题的所有权和状态。技术支持工程师和他们的经理每天都会监控他们的不正常问题，那些超过了正常反应或解决时间的问题会转到他们的管理层那里。\r\n</p>', '', ''),
(9, 'zhaopin', 0, '人才招聘', '<div class="top_bg">\r\n	<div class="main_top">\r\n		<br />\r\n	</div>\r\n</div>\r\n<div class="jianjie_main">\r\n	<div style="margin:10px 0;text-align:center;">\r\n	</div>\r\n</div>', '', ''),
(10, 'contact', 0, '联系我们', '<h5 style="font-size:14px;color:#666666;text-indent:2em;font-family:Simsun;">\r\n	对于任何问题，请随时通过邮件联系我们\r\n</h5>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	地址：上海市浦东新区唐镇上丰路977号电子商务港B座8层\r\n</p>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	电话：400-6238086 传真：021-60459869\r\n</p>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	邮编：201210\r\n</p>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	企业电子邮箱：nnw@nainaiwang.com\r\n</p>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	<br />\r\n</p>\r\n<p style="color:#666666;text-indent:2em;font-size:14px;font-family:Simsun;">\r\n	<img src="http://test.com/images/upload/image/20151203/20151203081617_61212.jpg" alt="" /> \r\n</p>', '', '');
